changeSet.sql:
CREATE TABLE IF NOT EXISTS public.product ( id BIGSERIAL PRIMARY KEY, name varchar(50) NOT NULL, amount varchar(10) NOT NULL, discount_percent integer, price varchar(10) NOT NULL );
--DROP TABLE IF EXISTS public.discount_card;
CREATE TABLE IF NOT EXISTS public.discount_card ( id BIGSERIAL PRIMARY KEY, discount integer NOT NULL );
--DROP TABLE IF EXISTS public."checks";
CREATE TABLE IF NOT EXISTS public."checks" ( id SERIAL PRIMARY KEY, total_price_with_discount varchar(15) NOT NULL, total_price varchar(15) NOT NULL, date timestamp, discount_amount integer, total_percent integer ); --DROP TABLE IF EXISTS public.product_information;
CREATE TABLE IF NOT EXISTS public.product_information ( id BIGSERIAL PRIMARY KEY, price_with_discount varchar(10) NOT NULL, check_id integer NOT NULL, product_id integer NOT NULL, total_price varchar(10) NOT NULL, total_price_with_discount varchar(10) NOT NULL, discount_percent integer NOT NULL, amount integer, CONSTRAINT "checks" FOREIGN KEY (check_id) REFERENCES public."checks" (id) MATCH SIMPLE ON UPDATE cascade ON DELETE cascade, CONSTRAINT product FOREIGN KEY (product_id) REFERENCES public.product (id) MATCH SIMPLE ON UPDATE NO ACTION ON DELETE NO ACTION );

